mapper.select:
select

from product
where 1=1

    and product_id = #{productId,jdbcType=INTEGER}


    and name = #{name,jdbcType=VARCHAR}


    and description = #{description,jdbcType=VARCHAR}


    and cn_name = #{cnName,jdbcType=VARCHAR}


    and cn_description = #{cnDescription,jdbcType=VARCHAR}


    and (category_id = #{categoryId,jdbcType=VARCHAR}
  or (category_id in (select category_id from category where parent_category = #{categoryId,jdbcType=VARCHAR}
  or (parent_category in (select category_id from category where parent_category = #{categoryId,jdbcType=VARCHAR} ))
  ))
  )



    and img_path = #{imgPath,jdbcType=VARCHAR}


    and view_img_path = #{viewImgPath,jdbcType=VARCHAR}


    and pre_img_path = #{preImgPath,jdbcType=VARCHAR}


    and priority = #{priority,jdbcType=INTEGER}


    and create_user = #{createUser,jdbcType=VARCHAR}


    and create_date = #{createDate,jdbcType=TIMESTAMP}


    and update_user = #{updateUser,jdbcType=VARCHAR}


    and update_date = #{updateDate,jdbcType=TIMESTAMP}
